import { Routes, Route, Link } from 'react-router-dom';
import './css/NewPost.css';
import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom'; 

function NewPost() {
  const [titleValue, SetTitleValue] = useState('');
  const [textValue, SetTextValue] = useState('');

  const CancelPostAdd = () => {
    navigate('/');
  }
  const PostAdd = async () => {
    const formData = new FormData();
    formData.append('id', idValue); // user_id 값
    formData.append('pwd', pwdValue);  // user_pw 값
    formData.append('userName', userNameValue);
    formData.append('profileImg', profileImg);

     try
    {
          const response = await fetch('https://myreactstudy1.dothome.co.kr/Register.php', 
              { method: 'POST',
                  body: formData,
              })
              if(!response.ok)
              {
                  alert(response.message);
              }

              const result = await response.json(); //비동기로 JSON에서 변환
              alert(result.message);
                
              if(result.status == 'succeed')
                  navigate('/');

    }
    catch(error)
    {
        console.log(error);
    }
  }

  const updateTitleValue = event => { SetTitleValue(event.target.value); };
  const updateTextValue = event => { SetTextValue(event.target.value); };

  return (
  <>
    <div className='title-class'>
      <p className='title-text-class'>제목 </p>
      <input className='title-input-class' onChange={updateTitleValue}/>
    </div>
    <input className='text-input-class' placeholder='내용을 입력 해주세요'/>
    <button onClick={PostAdd}>포스트 등록하기</button>
    <button onClick={CancelPostAdd}>메인으로 돌아가기</button>
  </>  
  );
}

export default NewPost;
