import { Routes, Route, Link } from 'react-router-dom';
import './css/NewPost.css';
import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom'; 
import {TextField, Button, Container, Typography, Box, Input} from '@mui/material';
import { PhotoCamera, Visibility } from '@mui/icons-material'

function NewPost() {
  const navigate = useNavigate();

  const [titleValue, SetTitleValue] = useState('');
  const [textValue, SetTextValue] = useState('');
  const [imgValue, SetImgValue] = useState('');
  const [idValue, SetIdValue] = useState(sessionStorage.getItem('SuserName') || '');
  const [bLoad, SetLoadBool] = useState(false);

  const CancelPostAdd = () => {
    navigate('/');
  }
  const PostAdd = async () => {
    if(idValue == '')
    {
      alert('로그인 여부를 확인해주세요');
      return;
    }

    const formData = new FormData();
    formData.append('userId', idValue);
    formData.append('title', titleValue); // user_id 값
    formData.append('text', textValue);  // user_pw 값
    if(imgValue != '')
      formData.append('img', imgValue);

    if(titleValue === '' || textValue === '')
    {
      alert('제목과 본문을 작성해주세요');
      return;
    }
    else alert('등록됨');

    try
    {
          const response = await fetch('https://myreactstudy1.dothome.co.kr/PostAdd.php', 
              { method: 'POST',
                  body: formData,
              })
              if(!response.ok)
              {
                  alert(response.message);
              }

              const result = await response.json(); //비동기로 JSON에서 변환
                
              if(result.status == 'succeed')
              {
                  alert('글이 등록되었습니다');
                  navigate('/');
              }
              else
              {
                  alert('글 등록에 실패했습니다');
              }
    }
    catch(error)
    {
        console.log(error);
    }
  }


  return (
    <Container maxWidth={"md"}
        sx={{
             bgcolor: 'background.paper', 
             borderRadius: 2, 
             boxShadow: 3,
             //상하 마진
             mt: 6,
             py: 6
          }}>
      <Typography 
        variant='h4'
        
      sx={{
        mr:2,
        flexGrow: 1,
        textAlign:"center",
      }}>새 글 작성</Typography>
      {/* 제목바 */}
      <Box
      sx={{
          display: 'flex', 
          flexDirection: 'row',
          alignItems: 'center', 
          mb: 3
      }}>
        <TextField 
          label="제목" 
          placeholder="제목을 입력해주세요" 
          onClick={(e) => SetTitleValue(e.target.value)}
        sx={{
              ml:2
        }}></TextField>
        <Button onClick={PostAdd} disabled={bLoad} sx={{
          bgcolor: '#3597ffff',
          color: 'black',
          height:'60px',
          px:'center'
        }}>글 등록하기</Button>
      </Box>
      <TextField
        fullWidth
        label="내용을 입력해주세요"
        multiline
        rows={15}
        onChange={(e) => SetTextValue(e.target.value)}
        sx={{mt: 3, mb: 3}}
      />
      <Box>
        {/*<Image sx={{SetImgValue : enab ? Visibility='false'}}/>*/}
        <Input type='file' accept="image/jpeg, image/jpg, image/gif, image/png"></Input>
      </Box>
    </Container>
  );
}

export default NewPost;
