import './css/Login.css';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import { Box, TextField, Button, Typography, Container  } from '@mui/material';


function Login(){
        const [idValue, SetIdValue] = React.useState('');
        const [pwdValue, SetPwdValue] = React.useState('');
        const [isLoginSucceed, SetLoginBool] = useState(false);
        const [data, SetData] = useState('');
    
        const updateIdValue = event => { SetIdValue(event.target.value); };
        const updatePwdValue = event => { SetPwdValue(event.target.value); };


         const navigate = useNavigate();

         const logEvent = () => {
            if(idValue !== "" && pwdValue !== "")
            {
                fetch(`https://myreactstudy1.dothome.co.kr/Login.php?id=${idValue}&pwd=${pwdValue}`, { method: 'POST',})
                .then((response) => {
                if(!response.ok)
                console.log(response.status);
                else
                    return response.json();
                })
                .then((result) => {
                    if(result['status'] == 'succeed')
                        navigate('/', { state: { isLogged: true, userId: idValue } });
                    else alert('ID나 비밀번호가 일치하지않습니다')
                })
    }
    }

    return(
    <Container sx={{bgcolor:'white', maxWidth:'400px'}}>
        {/*ID*/}    
        <Box sx={{}}>
            <Typography className=''> <b>ID:</b> </Typography>
            <TextField className="input-class" onChange={updateIdValue} sx={{bgcolor:'white'}}></TextField>
        </Box>
        {/*PW*/}
        <Box className = 'keydata-parent-class'>
            <Typography className=''> <b>비밀번호:</b> </Typography>
            <TextField className="input-class" type='password' white-space='nowrap' onChange={updatePwdValue} sx={{bgcolor:'white'}}></TextField>
        </Box>

        <button className='login-button-class-2' onClick={logEvent}> 로그인 </button>
        {/*LoginEvent(idValue, pwdValue)*/}
    </Container>
    );
}

export default Login;
