import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Button,
  Container,
  Grid,
  Typography,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  CircularProgress, // For loading indicator
  IconButton // For closing dialog if needed, though not used here
} from '@mui/material';
import { PhotoCamera } from '@mui/icons-material'; // Icon for image upload button

function NewPost() {
  const navigate = useNavigate();

  const [titleValue, SetTitleValue] = useState('');
  const [textValue, SetTextValue] = useState('');
  // const [imgValue, SetImgValue] = useState(''); // This state was not used in the original logic, directly using 'img' state
  const [idValue, SetIdValue] = useState(sessionStorage.getItem('SuserName') || '');
  const [imgFile, SetImgFile] = useState(null); // Changed name to imgFile for clarity, stores the File object
  const [preImg, SetPreImg] = useState(null); // Stores base64 string for image preview

  const fileInputRef = useRef(null); // Ref for the hidden file input

  // State for the custom dialog (alert replacement)
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogTitle, setDialogTitle] = useState('');
  const [dialogMessage, setDialogMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false); // State for loading indicator

  // Function to show the custom dialog
  const showDialog = (title, message) => {
    setDialogTitle(title);
    setDialogMessage(message);
    setDialogOpen(true);
  };

  // Function to close the custom dialog
  const handleCloseDialog = () => {
    setDialogOpen(false);
  };

  const CancelPostAdd = () => {
    navigate('/');
  };

  const PostAdd = async () => {
    if (idValue === '') {
      showDialog('알림', '로그인 여부를 확인해주세요.');
      return;
    }

    if (titleValue.trim() === '' || textValue.trim() === '') {
      showDialog('알림', '제목과 본문을 작성해주세요.');
      return;
    }

    setIsLoading(true); // Start loading

    const formData = new FormData();
    formData.append('userId', idValue);
    formData.append('title', titleValue);
    formData.append('text', textValue);
    if (imgFile) { // Check if an image file exists
      formData.append('img', imgFile);
    }

    try {
      const response = await fetch('https://myreactstudy1.dothome.co.kr/PostAdd.php', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        // Handle server errors that are not JSON responses
        showDialog('오류', `서버 응답 오류: ${response.statusText}`);
        return;
      }

      const result = await response.json(); //비동기로 JSON에서 변환

      if (result.status === 'succeed') {
        showDialog('성공', '글이 등록되었습니다.');
        // Navigate after user acknowledges the dialog
        setTimeout(() => {
          handleCloseDialog();
          navigate('/');
        }, 1500); // Give user a moment to read success message
      } else {
        showDialog('실패', result.message || '글 등록에 실패했습니다.');
      }
    } catch (error) {
      console.error(error);
      showDialog('오류', '글 등록 중 예기치 않은 오류가 발생했습니다.');
    } finally {
      setIsLoading(false); // End loading
    }
  };

  const updateImgEvent = event => {
    const file = event.target.files[0];
    if (!file) {
      SetImgFile(null);
      SetPreImg(null);
      return;
    }

    // 파일 확장자 확인
    const validImageTypes = ['image/jpeg', 'image/jpg', 'image/gif', 'image/png'];
    if (!validImageTypes.includes(file.type)) {
      showDialog('오류', '프로필 사진은 jpeg, jpg, png 타입의 이미지만 가능합니다.');
      // Clear the file input value so the same file can be selected again
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
      SetImgFile(null);
      SetPreImg(null);
      return;
    }

    // Check file size (e.g., max 3MB)
    const MAX_FILE_SIZE = 3 * 1024 * 1024; // 3MB
    if (file.size > MAX_FILE_SIZE) {
        showDialog('오류', '파일 크기가 3MB를 초과합니다.');
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
        SetImgFile(null);
        SetPreImg(null);
        return;
    }

    SetImgFile(file);
    encodeFileToBase64(file);
  };

  const encodeFileToBase64 = (file) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      SetPreImg(reader.result);
    };
    reader.onerror = (error) => {
      console.error('Error encoding file:', error);
      SetPreImg(null);
      showDialog('오류', '이미지 미리보기를 로드하는 데 실패했습니다.');
    };
  };

  const updateTitleValue = event => { SetTitleValue(event.target.value); };
  const updateTextValue = event => { SetTextValue(event.target.value); };

  return (
    <Container maxWidth="md" sx={{ py: 4, bgcolor: 'background.paper', borderRadius: 2, boxShadow: 3 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center" sx={{ mb: 4, color: 'primary.main', fontWeight: 'bold' }}>
        새 글 작성
      </Typography>

      {/* Title and Post Button Section */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <TextField
          fullWidth
          label="제목"
          variant="outlined"
          value={titleValue}
          onChange={updateTitleValue}
          placeholder="제목을 입력해주세요"
          sx={{ mr: 2, flexGrow: 1 }}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={PostAdd}
          disabled={isLoading} // Disable button while loading
          sx={{
            height: '56px', // Match TextField height
            px: 4,
            py: 1.5,
            fontSize: '1rem',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
            },
            transition: 'all 0.2s ease-in-out',
          }}
        >
          {isLoading ? <CircularProgress size={24} color="inherit" /> : '포스트 등록하기'}
        </Button>
      </Box>

      {/* Text Content Area */}
      <TextField
        fullWidth
        label="내용"
        variant="outlined"
        multiline
        rows={10}
        value={textValue}
        onChange={updateTextValue}
        placeholder="내용을 입력 해주세요"
        sx={{ mb: 3 }}
      />

      {/* Image Upload Section */}
      <Box
        sx={{
          border: '2px dashed',
          borderColor: 'grey.400',
          borderRadius: 2,
          p: 3,
          textAlign: 'center',
          mb: 3,
          bgcolor: 'grey.50',
          cursor: 'pointer',
          '&:hover': {
            borderColor: 'primary.main',
            bgcolor: 'grey.100',
          }
        }}
        onClick={() => fileInputRef.current.click()} // Click handler for the Box
      >
        <input
          type="file"
          ref={fileInputRef}
          onChange={updateImgEvent}
          style={{ display: 'none' }} // Hide the actual input
          accept="image/jpeg, image/jpg, image/gif, image/png"
        />
        {preImg ? (
          <Box sx={{ mb: 2 }}>
            <img src={preImg} alt="미리보기" style={{ maxWidth: '100%', maxHeight: '200px', borderRadius: '8px', objectFit: 'contain' }} />
          </Box>
        ) : (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <PhotoCamera sx={{ fontSize: 48, color: 'grey.600', mb: 1 }} />
            <Typography variant="body1" color="text.secondary" sx={{ fontWeight: 'medium' }}>
              클릭 또는 사진을 드롭해주세요
            </Typography>
            <Typography variant="caption" color="text.secondary">
              파일당 최대 크기 : 3MB (jpeg, jpg, gif, png만 가능)
            </Typography>
          </Box>
        )}
      </Box>

      {/* Lower Buttons Section */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
        <Button
          variant="outlined"
          color="secondary"
          onClick={CancelPostAdd}
          sx={{
            px: 4,
            py: 1.5,
            fontSize: '1rem',
            '&:hover': {
              backgroundColor: 'secondary.light',
              color: 'white'
            },
            transition: 'all 0.2s ease-in-out',
          }}
        >
          메인으로 돌아가기
        </Button>
      </Box>

      {/* Custom Dialog for Alerts */}
      <Dialog
        open={dialogOpen}
        onClose={handleCloseDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{dialogTitle}</DialogTitle>
        <DialogContent>
          <Typography id="alert-dialog-description">
            {dialogMessage}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary" autoFocus>
            확인
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default NewPost;
